          <rect x="1" y="1" width="16" height="16" rx="8" stroke="#FF141D" stroke-width="2"/>
          <rect x="4" y="4" width="10" height="10" rx="5" fill="#FF141D"/>
        </svg>
      </div>
      <div class="language" @click="changeLanguage('ru')">
        <span>{{$t('ruscha')}}</span>
        <svg class="passive" v-if="languageLocal2" width="18" height="18" viewBox="0 0 18 18" fill="none" xmlns="http://www.w3.org/2000/svg">
          <rect x="1" y="1" width="16" height="16" rx="8" stroke="#AEAEAE" stroke-width="2"/>
        </svg>
        <svg class="active" v-else width="18" height="18" viewBox="0 0 18 18" fill="none" xmlns="http://www.w3.org/2000/svg">
          <rect x="1" y="1" width="16" height="16" rx="8" stroke="#FF141D" stroke-width="2"/>
          <rect x="4" y="4" width="10" height="10" rx="5" fill="#FF141D"/>
        </svg>
      </div>
    </div>
  </div>
</template>

<script>
import {mapActions} from 'vuex';
export default {
  data(){
    return{
      languageLocal:true,
      languageLocal2:true,
      languageBack:false,
    }
  },
  mounted(){
    const height = window.innerHeight-536;
    const element = document.querySelector('.settings');
    element.style.minHeight = height+'px';
    this.changeLanguage(this.$i18n.locale)
  },
  methods:{
    ...mapActions({
      changeLang: 'languages/changeLanguage',
    }),
    changeLanguage(dil){
      if(dil == 'tm'){
        this.languageLocal = false;
        this.languageLocal2 = true;
      }else if(dil == 'ru'){  
        this.languageLocal = true;
        this.languageLocal2 = false;
      }
      this.changeLang(dil)
    },
    openLanguage(){
      this.languageBack = true;
    },
    closeLanguage(){
      this.languageBack = false;
    }
  }
}
</script>

